class FakeOpenAIResponses(OpenAIResponsesProtocol):
    def __init__(
        self,
        *,
        parsed_response: ParsedResponse[ProgramRecordGraph] | None = None,
        exc: Exception | None = None,
    ):
        self.parsed_response = parsed_response
        self.exc = exc
        self.calls: list[dict[str, Any]] = []

    def parse(
        self,
        *,
        model: str,
        temperature: float = 0,
        input: str | list[dict[str, Any]],
        text_format: Type[ProgramRecordGraph],
        max_output_tokens: int | None = None,
        **kwargs: Any,
    ) -> ParsedResponse[ProgramRecordGraph]:
        self.calls.append(
            {
                "model": model,
                "temperature": temperature,
                "input": input,
                "text_format": text_format,
                "max_output_tokens": max_output_tokens,
                "kwargs": kwargs,
            }
        )
        if self.exc:
            raise self.exc
        assert self.parsed_response is not None
        return self.parsed_response


class FakeOpenAIClient(OpenAIClientProtocol):
    def __init__(
        self,
        *,
        parsed_response: ParsedResponse[ProgramRecordGraph] | None = None,
        exc: Exception | None = None,
    ):
        self.responses = FakeOpenAIResponses(parsed_response=parsed_response, exc=exc)


class FakeTavilyClient(TavilyClientProtocol):
    def __init__(
        self,
        *,
        search_response: dict[str, Any] | None = None,
        extract_response: dict[str, Any] | None = None,
        search_exc: Exception | None = None,
        extract_exc: Exception | None = None,
    ):
        self.search_response = search_response or {"results": []}
        self.extract_response = extract_response or {
            "results": [],
            "failed_results": [],
        }
        self.search_exc = search_exc
        self.extract_exc = extract_exc

        self.search_calls: list[dict[str, Any]] = []
        self.extract_calls: list[dict[str, Any]] = []

    def search(
        self, query: str, max_results: int | None = None, **kwargs: Any
    ) -> dict[str, Any]:
        self.search_calls.append({"query": query, "max_results": max_results, **kwargs})
        if self.search_exc:
            raise self.search_exc
        return self.search_response

    def extract(
        self, urls: str | list[str], extract_depth: str | None = None, **kwargs: Any
    ) -> dict[str, Any]:
        self.extract_calls.append(
            {"urls": urls, "extract_depth": extract_depth, **kwargs}
        )
        if self.extract_exc:
            raise self.extract_exc
        return self.extract_response


