# Verifies that GraphRunner orchestrates its internal steps correctly and produces the expected final state when all external calls succeed. (OpenAI / Tavily / DB mocked)
def test_graph_runner_orchestration_happy_path():
    # --- Arrange ---

    agent_runs_repo = DummyAgentRunsRepo()
    tavily_client = FakeTavilyClient(
        search_response=SEARCH_RESPONSE_PHOTOGRAPHY,
        extract_response=EXTRACT_RESPONSE_PHOTOGRAPHY,
    )
    openai_client = FakeOpenAIClient(parsed_response=PARSED_RESPONSE_PHOTOGRAPHY)

    deps = GraphDeps(openai_client=openai_client, tavily_client=tavily_client)
    runner = GraphRunner(agent_runs_repo=agent_runs_repo, deps=deps)

    # --- Act ---
    request_id = "req_test_123"
    state = runner.run(
        request_id=request_id,
        payload={
            "query": "photography",
            "prefs": {"format": "online", "budget": "free", "city": "NYC"},
        },
    )

    # --- Assert ---

    # 1) final_state.results: buckets exist and filled
    assert "results" in state
    results = state["results"]
    assert set(results.keys()) == {"short_term", "medium_term", "long_term"}
    assert len(results["short_term"]) == 5
    assert len(results["medium_term"]) == 0
    assert len(results["long_term"]) == 0

    # 2) warnings
    assert state.get("warnings", []) == [
        "Path Organizer: One or more buckets are empty: medium_term, long_term."
    ]

    # 3) verify internal calls (by order)
    agent_names = [r.agent_name for r in agent_runs_repo.runs]
    assert agent_names == ["scout", "extract", "organize"]

    # 4) AgentRunDoc fields: request_id, timing, no errors, step-only warnings
    assert len(agent_runs_repo.runs) == 3
    for r in agent_runs_repo.runs:
        assert r.request_id == request_id
        assert isinstance(r.started_at, datetime)
        assert r.ended_at is not None and r.ended_at >= r.started_at
        assert r.error is None
        if r.agent_name == "organize":
            assert r.warnings == [
                "Path Organizer: One or more buckets are empty: medium_term, long_term."
            ]
        else:
            assert r.warnings == []
        assert r.output_summary is not None

    # 5) external calls happened
    assert len(tavily_client.search_calls) == 3  # number of queries
    assert len(tavily_client.extract_calls) == 1  # batch extract called once
    assert len(openai_client.responses.calls) == 5  # one per URL


